mapper.select:
SELECT MIX.T_PERSTATUS,MIX2.C_PERSTATUS,S_PERSTATUS,M.M_NAME, ROWNUM FROM (SELECT NVL(T_PERSTATUS,'NOINFO') T_PERSTATUS FROM TEACHER RIGHT OUTER JOIN DUAL ON T_NO=99) MIX , ( SELECT NVL(C_PERSTATUS,'NOINFO'), C_PERSTATUS FROM CLASS RIGHT OUTER JOIN DUAL ON T_NO=99
ORDER BY C_REGDATE DESC
) MIX2,(SELECT NVL(S_PERSTATUS,'NOINFO') S_PERSTATUS FROM SPACE RIGHT OUTER JOIN DUAL ON H_NO=99) S,(SELECT M_NAME FROM MEMBERINFO WHERE M_NO=99) M WHERE ROWNUM=1
